// Parse and format multi-modal intelligence results
const output = JSON.parse($input.first().json.stdout);

// Extract key insights
const insights = {
  analysis_type: 'multimodal_intelligence',
  timestamp: new Date().toISOString(),
  success: output.success,
  content_analyzed: {
    images: output.content_summary?.total_images_analyzed || 0,
    videos: output.content_summary?.total_videos_analyzed || 0,
    total: output.content_summary?.total_content_pieces || 0
  },
  market_insights: output.unified_report?.unified_market_insights || {},
  recommendations: output.unified_report?.actionable_recommendations || [],
  cross_modal_trends: output.content_summary?.cross_modal_trends_found || 0,
  shared_brands: output.content_summary?.shared_brands_identified || 0
};

return insights;
